mapper.select:
select
  btype_code,
  btype_name,
  create_time,
  update_time,
  create_by,
  update_by,
btype_sort_name
from t_btype_info


        AND btype_code = #{queryDTO.btypeCode}


        AND INSTR(btype_name,#{queryDTO.btypeName})


        AND btype_sort_name = #{queryDTO.btypeSortName}


        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}

select
   btype_code,
   btype_name,
   create_time,
   update_time,
    create_by,
    update_by,
btype_sort_name
from t_btype_info


        AND btype_code = #{queryDTO.btypeCode}


        AND INSTR(btype_name,#{queryDTO.btypeName})


        AND btype_sort_name = #{queryDTO.btypeSortName}


        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}
