mapper.select:
select
  inv_code,
  inv_name,
  inv_unit,
  sort_code,
  sort_name,
  brand_code,
  brand_name,
  create_time,
  update_time,
create_by,
update_by
from t_inventory_info


        AND INSTR(inv_name,#{queryDTO.invName})


        AND inv_code = #{queryDTO.invCode}


        AND sort_code = #{queryDTO.sortCode}


        AND INSTR(sort_name,#{queryDTO.sortName})


        AND INSTR(brand_name,#{queryDTO.brandName})


        AND brand_code = #{queryDTO.brandCode}


        AND inv_unit = #{queryDTO.invUnit}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}

select
   inv_code,
   inv_name,
   inv_unit,
   sort_code,
   sort_name,
   brand_code,
   brand_name,
   create_time,
   update_time,
create_by,
update_by
from t_inventory_info


        AND INSTR(inv_name,#{queryDTO.invName})


        AND inv_code = #{queryDTO.invCode}


        AND sort_code = #{queryDTO.sortCode}


        AND INSTR(sort_name,#{queryDTO.sortName})


        AND INSTR(brand_name,#{queryDTO.brandName})


        AND brand_code = #{queryDTO.brandCode}


        AND inv_unit = #{queryDTO.invUnit}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}
