mapper.select:
select
  bill_code,
  btype_code,
  btype_name,
  order_date,
  wh_code,
  wh_name,
  delivery_date,
  ot_name,
create_by,
create_time,
update_by,
update_time
from t_order_info


        AND bill_code = #{queryDTO.billCode}


        AND btype_code = #{queryDTO.btypeCode}


        AND INSTR(btype_name,#{queryDTO.btypeName})


        AND order_date = #{queryDTO.orderDate}


        AND wh_code = #{queryDTO.whCode}


        AND INSTR(wh_name,#{queryDTO.whName})


        AND delivery_date = #{queryDTO.deliveryDate}


        AND ot_name = #{queryDTO.otName}



        AND create_time >= #{queryDTO.createTimeBegin}


        AND create_time <= #{queryDTO.createTimeEnd}



        AND update_time >= #{queryDTO.updateTimeBegin}


        AND update_time <= #{queryDTO.updateTimeEnd}

select
